sys_script_include.script:
var WorkOrderHistoryHelper = Class.create();
WorkOrderHistoryHelper.prototype = {
	initialize: function() {
		this.tableName = "sn_service_request_work_order_history";
	},

	addEntry: function(workOrderSysId, state) {
		var workHistoryEntry = new GlideRecord(this.tableName);
		workHistoryEntry.initialize();
		workHistoryEntry.work_order = workOrderSysId;
		workHistoryEntry.state = state;
		workHistoryEntry.insert();

		// todo: need to figure out a cascade-delete strategy
	},

	type: 'WorkOrderHistoryHelper'
};
