sys_script_include.script:
var Capability = Class.create();
Capability.prototype = {
	initialize: function(kind, name, version) {
		this.kind = kind;
		this.name = name;
		this.version = version;
	},

	kind : this.type,
	name : this.name,
	version : this.version,

	toString : function() {
		return "kind:" + this.kind + ", name:" + this.name + ", version:" + this.version;	
	},

	hashCode : function() {
		var str = this.displayValue(this);
		var hash = 0, i;
		if (str.length == 0) return hash;
		for (i = 0; i < str.length; i++) {
			var ch = str.charCodeAt(i);
			hash = ((hash << 5) - hash) + ch;
			hash = hash & hash;
		}
		return hash;
	},

	type: "Capability"
};
